    void respond()
    {

        if (M5.BtnC.read())
        {if (sync_BtnC)
            {  BUTTONS::drawRightButton(BLACK, WHITE);
                sync_BtnC = false;
            }
        }
        else
        {if (!sync_BtnC)
            {   BUTTONS::drawRightButton(WHITE, BLUE);
                sync_BtnC = true;
            }
        }

            if (M5.BtnB.read())
            {if (sync_BtnB)
                {   BUTTONS::drawBellButton(BLACK, WHITE);
                    sync_BtnB = false;
                }
            }
            else
            {if (!sync_BtnB)
                {   BUTTONS::drawBellButton(RED, WHITE);
                    sync_BtnB = true;
                }
            }

            if (M5.BtnA.read())
            {if (sync_BtnA)
                {BUTTONS::drawLeftButton(BLACK, WHITE);
                 sync_BtnA = false;
                }
            }
            else
            {if (!sync_BtnA)
                {   BUTTONS::drawLeftButton(GREEN,BLACK);
                    sync_BtnA = true;
                }
            }
    }

